const changeTheme = () => {
    let themeSwitcherBtn = document.getElementsByClassName("theme-switcher")[0];
    let htmlElem = document.getElementsByTagName("html")[0];

    if (themeSwitcherBtn.getAttribute("aria-label") == "Turn off dark mode") {
        htmlElem.setAttribute("data-theme", "light")
        themeSwitcherBtn.setAttribute("aria-label", "Turn on dark mode")
        themeSwitcherBtn.innerHTML = "<i>Turn on dark mode</i>"
    }
    else {
        htmlElem.setAttribute("data-theme", "dark")
        themeSwitcherBtn.setAttribute("aria-label", "Turn off dark mode")
        themeSwitcherBtn.innerHTML = "<i>Turn off dark mode</i>"
    }
}

window.changeTheme = changeTheme